import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';

/**
 * Componente de Navegação Principal (Nav.js)
 * Renderiza a barra superior de navegação com links para as rotas da aplicação.
 * Usa Tailwind CSS para estilização.
 */
const Nav = () => {
  const { user, logout, hasPermission } = useAuth();
  
  const linkClass = "hover:text-blue-400 transition-colors p-2 rounded-lg";
  const activeClass = "bg-gray-700 text-blue-300 shadow-inner";

  // Função utilitária para determinar se o link está ativo (simples)
  const isLinkActive = (path) => {
    return window.location.pathname === path ? activeClass : "text-white";
  };

  const handleLogout = async () => {
    if (window.confirm('Tem certeza que deseja sair?')) {
      await logout();
    }
  };
  
  return (
    <nav className="bg-gray-800 p-4 shadow-lg sticky top-0 z-10">
      <div className="flex justify-between items-center">
        {/* Links de navegação */}
        <ul className="flex space-x-6 text-white font-medium">
          <li>
            <Link to="/" className={`${linkClass} ${isLinkActive('/')}`}>
              🏠 Início
            </Link>
          </li>
          <li>
            <Link to="/consulta" className={`${linkClass} ${isLinkActive('/consulta')}`}>
              � Consulta Dinâmica
            </Link>
          </li>
          <li>
            <Link to="/lista-familias" className={`${linkClass} ${isLinkActive('/lista-familias')}`}>
              👨‍👩‍👧‍👦 Lista de Famílias
            </Link>
          </li>
          
          {/* Links que requerem permissões específicas */}
          {hasPermission(['monitor', 'coordenador', 'admin']) && (
            <li>
              <Link to="/cadastro" className={`${linkClass} ${isLinkActive('/cadastro')}`}>
                ➕ Cadastrar Família
              </Link>
            </li>
          )}

          {/* Links administrativos */}
          {hasPermission(['admin', 'coordenador']) && (
            <>
              <li>
                <Link to="/cadastro-monitor" className={`${linkClass} ${isLinkActive('/cadastro-monitor')}`}>
                  👤 Cadastrar Monitor
                </Link>
              </li>
              <li>
                <Link to="/monitores" className={`${linkClass} ${isLinkActive('/monitores')}`}>
                  👥 Lista de Monitores
                </Link>
              </li>
            </>
          )}
        </ul>

        {/* Informações do usuário e logout */}
        <div className="flex items-center space-x-4 text-white">
          <div className="text-sm">
            <span className="text-gray-300">Olá, </span>
            <span className="font-semibold">{user?.nome_completo || user?.username}</span>
            <span className="text-xs text-gray-400 ml-2">
              ({user?.tipo_usuario})
            </span>
          </div>
          
          <button
            onClick={handleLogout}
            className="bg-red-600 hover:bg-red-700 px-3 py-1 rounded text-sm transition-colors"
          >
            🚪 Sair
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Nav;
