import React, { useState, useEffect } from 'react';
import { validateCompleteForm, formatErrorMessages } from '../utils/validationHelpers';
import MembrosList from './MembrosList';
import './Formulario.css';

function Formulario({ onSave, disabled = false, dadosIniciais = null, modoEdicao = false }) {
    // Estado para armazenar os dados do formul치rio baseado na tabela Familia do Oracle
    const [formData, setFormData] = useState({
        // Dados da fam칤lia (tabela Familia)
        nome_familia: '',
        migracao: '',
        estado_origem: '',
        cidade_origem: '',
        recebe_beneficio: 0,
        possui_plano_saude: 0,
        convenio: '',
        observacoes: '',
        
        // Dados do endere칞o (tabela Endereco - 1:1 com Familia)
        endereco: {
            quadra: '',
            rua: '',
            numero_casa: '',
            complemento: '',
            id_area: ''
        },
        
        // Dados de animais (tabela Animal - 1:1 com Familia)
        animal: {
            tem_animal: 0,
            qtd_animais: '',
            qual_animal: ''
        },
        
        // Dados de estrutura da habita칞칚o (tabela EstruturaHabitacao - 1:1 com Familia)
        estrutura: {
            tipo_habitacao: '',
            tipo_lote: '',
            situacao_convivencia: '',
            energia_eletrica: 0,
            material_parede: '',
            material_piso: '',
            material_cobertura: '',
            qtd_quartos: '',
            qtd_camas: '',
            tipo_camas: ''
        },
        
        // Dados de saneamento (tabela RecursoSaneamento - 1:1 com Familia)
        saneamento: {
            horta: 0,
            arvore_frutifera: 0,
            como_escoa: '',
            tem_banheiro: 0,
            dest_lixo: '',
            bebe_agua: '',
            trata_agua: ''
        }
    });

    // Lista de membros da fam칤lia
    const [membros, setMembros] = useState([]);
    
    // Dados de entrevista
    const [entrevistaData, setEntrevistaData] = useState({
        data_entrevista: new Date().toISOString().split('T')[0],
        entrevistado: '',
        telefone_contato: '',
        observacoes: ''
    });

    const [areas, setAreas] = useState([]);
    const [validationErrors, setValidationErrors] = useState([]);
    const [isSubmitting, setIsSubmitting] = useState(false);

    // Buscar 치reas dispon칤veis para o select
    useEffect(() => {
        const fetchAreas = async () => {
            try {
                const response = await fetch(`${process.env.REACT_APP_API_BASE_URL || 'http://localhost:3001/api'}/dados/Area`);
                if (response.ok) {
                    const areasData = await response.json();
                    setAreas(areasData);
                }
            } catch (error) {
                console.error('Erro ao buscar 치reas:', error);
            }
        };
        fetchAreas();
    }, []);

    // Carregar dados iniciais quando em modo edi칞칚o
    useEffect(() => {
        if (modoEdicao && dadosIniciais) {
            console.log('游댃 Carregando dados iniciais no formul치rio:', dadosIniciais);
            setFormData(prevData => ({
                ...prevData,
                ...dadosIniciais
            }));
        }
    }, [dadosIniciais, modoEdicao]);

    // Fun칞칚o que atualiza o estado a cada mudan칞a nos campos
    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        
        if (name.startsWith('entrevista.')) {
            // Para campos de entrevista
            const field = name.split('.')[1];
            setEntrevistaData(prevData => ({
                ...prevData,
                [field]: value
            }));
        } else if (name.includes('.')) {
            // Para campos aninhados (endereco.rua, animal.tem_animal, etc.)
            const [section, field] = name.split('.');
            setFormData(prevData => ({
                ...prevData,
                [section]: {
                    ...prevData[section],
                    [field]: type === 'checkbox' ? (checked ? 1 : 0) : 
                            type === 'number' ? (value === '' ? '' : Number(value)) : value
                }
            }));
        } else {
            // Para campos do n칤vel raiz
            setFormData(prevData => ({
                ...prevData,
                [name]: type === 'checkbox' ? (checked ? 1 : 0) : 
                        type === 'number' ? (value === '' ? '' : Number(value)) : value
            }));
        }
    };

    // Fun칞칚o que lida com o envio do formul치rio
    const handleSubmit = (e) => {
        e.preventDefault();
        
        // Limpar erros anteriores
        setValidationErrors([]);
        
        // Validar dados antes do envio
        const validation = validateCompleteForm(formData);
        
        if (!validation.isValid) {
            setValidationErrors(validation.errors);
            // Scroll para o topo para mostrar os erros
            window.scrollTo({ top: 0, behavior: 'smooth' });
            return;
        }
        
        setIsSubmitting(true);
        
        // Estruturar os dados para envio conforme esperado pelo backend
        const dadosParaEnvio = {
            // Dados principais da fam칤lia
            nome_familia: formData.nome_familia,
            migracao: formData.migracao,
            estado_origem: formData.estado_origem,
            cidade_origem: formData.cidade_origem,
            recebe_beneficio: formData.recebe_beneficio,
            possui_plano_saude: formData.possui_plano_saude,
            convenio: formData.convenio,
            observacoes: formData.observacoes,
            
            // Dados relacionados (ser칚o tratados pelo backend)
            endereco: formData.endereco,
            animal: formData.animal,
            estrutura: formData.estrutura,
            saneamento: formData.saneamento,
            
            // Membros da fam칤lia
            membros: membros,
            
            // Dados da entrevista
            entrevista: entrevistaData
        };

        onSave(dadosParaEnvio);
        setIsSubmitting(false);
    };

    return (
        <div className="formulario-container">
            <h2>Cadastro Completo de Fam칤lia</h2>
            
            {/* Exibir erros de valida칞칚o */}
            {validationErrors.length > 0 && (
                <div className="validation-errors">
                    <h3>丘멆잺 Corrija os seguintes problemas:</h3>
                    <div className="error-list">
                        {formatErrorMessages(validationErrors)}
                    </div>
                </div>
            )}
            
            <form onSubmit={handleSubmit} className="familia-form">
                
                {/* Se칞칚o: Dados B치sicos da Fam칤lia */}
                <fieldset className="form-section">
                    <legend>Dados B치sicos da Fam칤lia</legend>
                    
                    <div className="form-group">
                        <label htmlFor="nome_familia">Nome da Fam칤lia: *</label>
                        <input
                            type="text"
                            id="nome_familia"
                            name="nome_familia"
                            value={formData.nome_familia}
                            onChange={handleChange}
                            required
                            maxLength="150"
                            disabled={disabled || isSubmitting}
                        />
                    </div>

                    <div className="form-row">
                        <div className="form-group">
                            <label htmlFor="migracao">Migra칞칚o:</label>
                            <input
                                type="text"
                                id="migracao"
                                name="migracao"
                                value={formData.migracao}
                                onChange={handleChange}
                                maxLength="50"
                                placeholder="Ex: Rural para urbana"
                            />
                        </div>
                        
                        <div className="form-group">
                            <label htmlFor="estado_origem">Estado de Origem:</label>
                            <input
                                type="text"
                                id="estado_origem"
                                name="estado_origem"
                                value={formData.estado_origem}
                                onChange={handleChange}
                                maxLength="80"
                            />
                        </div>
                        
                        <div className="form-group">
                            <label htmlFor="cidade_origem">Cidade de Origem:</label>
                            <input
                                type="text"
                                id="cidade_origem"
                                name="cidade_origem"
                                value={formData.cidade_origem}
                                onChange={handleChange}
                                maxLength="80"
                            />
                        </div>
                    </div>

                    <div className="form-row">
                        <div className="form-group checkbox-group">
                            <label>
                                <input
                                    type="checkbox"
                                    name="recebe_beneficio"
                                    checked={formData.recebe_beneficio === 1}
                                    onChange={handleChange}
                                />
                                Recebe Benef칤cio Social
                            </label>
                        </div>
                        
                        <div className="form-group checkbox-group">
                            <label>
                                <input
                                    type="checkbox"
                                    name="possui_plano_saude"
                                    checked={formData.possui_plano_saude === 1}
                                    onChange={handleChange}
                                />
                                Possui Plano de Sa칰de
                            </label>
                        </div>
                    </div>

                    {formData.possui_plano_saude === 1 && (
                        <div className="form-group">
                            <label htmlFor="convenio">Conv칡nio:</label>
                            <input
                                type="text"
                                id="convenio"
                                name="convenio"
                                value={formData.convenio}
                                onChange={handleChange}
                                maxLength="120"
                            />
                        </div>
                    )}

                    <div className="form-group">
                        <label htmlFor="observacoes">Observa칞칫es:</label>
                        <textarea
                            id="observacoes"
                            name="observacoes"
                            value={formData.observacoes}
                            onChange={handleChange}
                            rows="3"
                            placeholder="Observa칞칫es gerais sobre a fam칤lia..."
                        />
                    </div>
                </fieldset>

                {/* Se칞칚o: Endere칞o */}
                <fieldset className="form-section">
                    <legend>Endere칞o</legend>
                    
                    <div className="form-row">
                        <div className="form-group">
                            <label htmlFor="endereco.id_area">츼rea:</label>
                            <select
                                id="endereco.id_area"
                                name="endereco.id_area"
                                value={formData.endereco.id_area}
                                onChange={handleChange}
                            >
                                <option value="">Selecione uma 치rea</option>
                                {areas.map(area => (
                                    <option key={area.ID_AREA} value={area.ID_AREA}>
                                        {area.NOME_AREA}
                                    </option>
                                ))}
                            </select>
                        </div>
                        
                        <div className="form-group">
                            <label htmlFor="endereco.quadra">Quadra:</label>
                            <input
                                type="text"
                                id="endereco.quadra"
                                name="endereco.quadra"
                                value={formData.endereco.quadra}
                                onChange={handleChange}
                                maxLength="30"
                            />
                        </div>
                    </div>

                    <div className="form-row">
                        <div className="form-group">
                            <label htmlFor="endereco.rua">Rua:</label>
                            <input
                                type="text"
                                id="endereco.rua"
                                name="endereco.rua"
                                value={formData.endereco.rua}
                                onChange={handleChange}
                                maxLength="100"
                            />
                        </div>
                        
                        <div className="form-group">
                            <label htmlFor="endereco.numero_casa">N칰mero da Casa:</label>
                            <input
                                type="text"
                                id="endereco.numero_casa"
                                name="endereco.numero_casa"
                                value={formData.endereco.numero_casa}
                                onChange={handleChange}
                                maxLength="20"
                            />
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="endereco.complemento">Complemento:</label>
                        <input
                            type="text"
                            id="endereco.complemento"
                            name="endereco.complemento"
                            value={formData.endereco.complemento}
                            onChange={handleChange}
                            maxLength="150"
                            placeholder="Ex: Pr칩ximo ao mercado, ao lado da escola..."
                        />
                    </div>
                </fieldset>

                {/* Se칞칚o: Animais */}
                <fieldset className="form-section">
                    <legend>Animais</legend>
                    
                    <div className="form-group checkbox-group">
                        <label>
                            <input
                                type="checkbox"
                                name="animal.tem_animal"
                                checked={formData.animal.tem_animal === 1}
                                onChange={handleChange}
                            />
                            Possui Animais
                        </label>
                    </div>

                    {formData.animal.tem_animal === 1 && (
                        <div className="form-row">
                            <div className="form-group">
                                <label htmlFor="animal.qtd_animais">Quantidade de Animais:</label>
                                <input
                                    type="number"
                                    id="animal.qtd_animais"
                                    name="animal.qtd_animais"
                                    value={formData.animal.qtd_animais}
                                    onChange={handleChange}
                                    min="1"
                                />
                            </div>
                            
                            <div className="form-group">
                                <label htmlFor="animal.qual_animal">Tipos de Animais:</label>
                                <input
                                    type="text"
                                    id="animal.qual_animal"
                                    name="animal.qual_animal"
                                    value={formData.animal.qual_animal}
                                    onChange={handleChange}
                                    maxLength="30"
                                    placeholder="Ex: C칚es, Gatos, Galinhas..."
                                />
                            </div>
                        </div>
                    )}
                </fieldset>

                {/* Se칞칚o: Estrutura da Habita칞칚o */}
                <fieldset className="form-section">
                    <legend>Estrutura da Habita칞칚o</legend>
                    
                    <div className="form-row">
                        <div className="form-group">
                            <label htmlFor="estrutura.tipo_habitacao">Tipo de Habita칞칚o:</label>
                            <select
                                id="estrutura.tipo_habitacao"
                                name="estrutura.tipo_habitacao"
                                value={formData.estrutura.tipo_habitacao}
                                onChange={handleChange}
                            >
                                <option value="">Selecione</option>
                                <option value="Casa">Casa</option>
                                <option value="Apartamento">Apartamento</option>
                                <option value="Barraco">Barraco</option>
                                <option value="Outro">Outro</option>
                            </select>
                        </div>
                        
                        <div className="form-group">
                            <label htmlFor="estrutura.tipo_lote">Tipo de Lote:</label>
                            <select
                                id="estrutura.tipo_lote"
                                name="estrutura.tipo_lote"
                                value={formData.estrutura.tipo_lote}
                                onChange={handleChange}
                            >
                                <option value="">Selecione</option>
                                <option value="Pr칩prio">Pr칩prio</option>
