                {/* Se칞칚o: Endere칞o */}
                <fieldset className="form-section">
                    <legend>Endere칞o</legend>
                    
                    <div className="form-row">
                        <div className="form-group">
                            <label htmlFor="endereco.id_area">츼rea:</label>
