                            <select
                                id="estrutura.tipo_lote"
                                name="estrutura.tipo_lote"
                                value={formData.estrutura.tipo_lote}
                                onChange={handleChange}
                            >
                                <option value="">Selecione</option>
                                <option value="Pr칩prio">Pr칩prio</option>
