import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';

/**
 * Componente de Navega√ß√£o Principal (Nav.js)
 * Renderiza a barra superior de navega√ß√£o com links para as rotas da aplica√ß√£o.
 * Usa Tailwind CSS para estiliza√ß√£o.
 */
const Nav = () => {
  const { user, logout, hasPermission } = useAuth();
  
  const linkClass = "hover:text-blue-400 transition-colors p-2 rounded-lg";
  const activeClass = "bg-gray-700 text-blue-300 shadow-inner";

  // Fun√ß√£o utilit√°ria para determinar se o link est√° ativo (simples)
  const isLinkActive = (path) => {
    return window.location.pathname === path ? activeClass : "text-white";
  };

  const handleLogout = async () => {
    if (window.confirm('Tem certeza que deseja sair?')) {
      await logout();
    }
  };
  
  return (
    <nav className="bg-gray-800 p-4 shadow-lg sticky top-0 z-10">
      <div className="flex justify-between items-center">
        {/* Links de navega√ß√£o */}
        <ul className="flex space-x-6 text-white font-medium">
          <li>
            <Link to="/" className={`${linkClass} ${isLinkActive('/')}`}>
              üè† In√≠cio
            </Link>
          </li>
          <li>
            <Link to="/consulta" className={`${linkClass} ${isLinkActive('/consulta')}`}>
              üìä Consulta (Fam√≠lias)
            </Link>
          </li>
          
          {/* Links que requerem permiss√µes espec√≠ficas */}
          {hasPermission(['monitor', 'coordenador', 'admin']) && (
            <li>
              <Link to="/cadastro" className={`${linkClass} ${isLinkActive('/cadastro')}`}>
                ‚ûï Cadastrar Fam√≠lia
              </Link>
            </li>
          )}
          
          <li>
            <Link to="/consulta-avancada" className={`${linkClass} ${isLinkActive('/consulta-avancada')}`}>
              ‚öôÔ∏è Consulta Din√¢mica
            </Link>
          </li>

          {/* Links administrativos */}
          {hasPermission(['admin', 'coordenador']) && (
            <li>
              <Link to="/usuarios" className={`${linkClass} ${isLinkActive('/usuarios')}`}>
                üë• Usu√°rios
              </Link>
            </li>
          )}
        </ul>

        {/* Informa√ß√µes do usu√°rio e logout */}
        <div className="flex items-center space-x-4 text-white">
          <div className="text-sm">
            <span className="text-gray-300">Ol√°, </span>
            <span className="font-semibold">{user?.nome_completo || user?.username}</span>
            <span className="text-xs text-gray-400 ml-2">
              ({user?.tipo_usuario})
            </span>
          </div>
          
          <button
            onClick={handleLogout}
            className="bg-red-600 hover:bg-red-700 px-3 py-1 rounded text-sm transition-colors"
          >
            üö™ Sair
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Nav;
